proc glimmix data=v; 
_var_=2*_mu_*_mu_/(n-1); 
model variance=x/noint solution; 
output out=v predicted=p;
run; 
proc sgplot data=v; 
scatter y=variance x=mean; 
reg y=p x=mean/degree=2; 
run;
